require('should');
var encryptor = require('./index')('12345-abcde');

describe('encrypt', function() {
  it('encrypts the value', function() {
    'hello world'.should.not.eql(encryptor.encrypt('hello world'));
  });
});

describe('decrypt', function() {
  it('decrypts the value', function() {
    encryptor.decrypt(encryptor.encrypt('hello world')).should.eql('hello world');
  });
});
